import React, {useState} from 'react';
import './WorkArea.css';

function WorkArea() {
    // series of tabs with different editors monaco representing different file types
    // each tab has a close button
    // each tab has a title

    const [tabs, setTabs] = useState([{
        title: 'Tab 1', content: 'This is tab 1', type: 'text',
    }]);
    const [activeTab, setActiveTab] = useState(null);

    const handleTabClick = (tab) => {
        setActiveTab(tab);
    }

    const handleCloseTab = (tab) => {
        setTabs(tabs.filter(t => t !== tab));
        if (activeTab === tab) {
            setActiveTab(null);
        }
    }

    const handleNewTab = () => {
        const newTab = {
            title: 'New Tab', content: '', type: 'text',
        };
        setTabs([...tabs, newTab]);
        setActiveTab(newTab);
    }

    const handleTabChange = (content) => {
        const updatedTabs = tabs.map(t => t === activeTab ? {...t, content} : t);
        setTabs(updatedTabs);
    }

    return (<div className="work-area">
            <div className="tabs">
                {tabs.map(tab => (<div
                        key={tab.title}
                        className={`tab ${activeTab === tab ? 'active' : ''}`}
                        onClick={() => handleTabClick(tab)}
                    >
                        <span>{tab.title}</span>
                        <button onClick={() => handleCloseTab(tab)}>X</button>
                    </div>))}
                <button onClick={handleNewTab}>+</button>
            </div>
            <div className="editor">
                {activeTab && (<textarea
                        value={activeTab.content}
                        onChange={(e) => handleTabChange(e.target.value)}
                    />)}
            </div>
        </div>);

}

export default WorkArea;
