import React, {useState} from 'react';

import SideBar from './components/SideBar';
import WorkArea from './components/WorkArea';
import './App.css';

function App() {

    const [tabs, setTabs] = useState([{
        title: 'Tab 1', content: 'This is tab 1', language: 'java'
    }
    ]);

    const [activeTab, setActiveTab] = useState(tabs[0]);

    const handleNewTab = () => {
        const newTab = {
            title: `Tab ${tabs.length + 1}`,
            content: '',
            language: 'java'
        };
        setTabs([...tabs, newTab]);
        setActiveTab(newTab);
    }

    return (
        <div className="App" style={{display: 'flex'}}>
            <SideBar/>
            <WorkArea handleNewTab={handleNewTab} tabs={tabs} setTabs={setTabs} activeTab={activeTab} setActiveTab={setActiveTab}/>
        </div>
    );
}

export default App;
