import React from "react";
import PropTypes from "prop-types";

import "./TabBar.css";

function TabBar({tabs, activeTab, onTabClick, onCloseTab, onNewTab, onTabChange}) {
    return (
        <div className="tab-bar">
            <div className="tab-bar-tabs">
                {tabs.map(tab => (
                    <div
                        key={tab.title}
                        className={`tab ${activeTab === tab ? 'active' : ''}`}
                        onClick={() => onTabClick(tab)}
                    >
                        {tab.title}
                        <button onClick={() => onCloseTab(tab)}>X</button>
                    </div>
                ))}
                <button onClick={onNewTab}>+</button>
            </div>
            <div className="tab-bar-content">
                <textarea
                    value={activeTab ? activeTab.content : ''}
                    onChange={(e) => onTabChange(e.target.value)}
                />
            </div>
        </div>
    );
}

TabBar.propTypes = {
    tabs: PropTypes.arrayOf(PropTypes.shape({
        title: PropTypes.string.isRequired,
        content: PropTypes.string.isRequired,
        type: PropTypes.string.isRequired,
    })).isRequired,
    activeTab: PropTypes.object,
    onTabClick: PropTypes.func.isRequired,
    onCloseTab: PropTypes.func.isRequired,
    onNewTab: PropTypes.func.isRequired,
    onTabChange: PropTypes.func.isRequired,
};

export default TabBar;
