import React, { useState } from 'react';
import './WorkArea.css';
import TabBar from './TabBar';
import Editor from '@monaco-editor/react';

function WorkArea() {
    const [tabs, setTabs] = useState([
        { title: 'Tab 1', content: 'This is tab 1', type: 'text' }
    ]);
    const [activeTab, setActiveTab] = useState(tabs[0]);

    const handleTabClick = (tab) => {
        setActiveTab(tab);
    };

    const handleCloseTab = (tab) => {
        const remainingTabs = tabs.filter(t => t !== tab);
        setTabs(remainingTabs);
        if (tab === activeTab) {
            setActiveTab(remainingTabs.length > 0 ? remainingTabs[0] : null);
        }
    };

    const handleNewTab = () => {
        const newTab = {
            title: `Tab ${tabs.length + 1}`,
            content: '',
            type: 'text',
        };
        setTabs([...tabs, newTab]);
        setActiveTab(newTab);
    };

    const handleTabChange = (tab, value) => {
        const newTabs = tabs.map(t => t === tab ? { ...t, content: value } : t);
        setTabs(newTabs);
    };

    return (
        <div className="work-area">
            <TabBar
                tabs={tabs}
                activeTab={activeTab}
                onTabClick={handleTabClick}
                onCloseTab={handleCloseTab}
                onNewTab={handleNewTab}
            />
            <div className="editor-container">
                {activeTab && (
                    <Editor
                        height="100%"
                        defaultLanguage="java"
                        value={activeTab.content}
                        onChange={(value) => handleTabChange(activeTab, value)}
                    />
                )}
            </div>
        </div>
    );
}

export default WorkArea;
