import React, {useState} from 'react';
import './WorkArea.css';

import TabBar from './TabBar';
import {Editor} from "@monaco-editor/react";

function WorkArea() {

    const [tabs, setTabs] = useState([{
        title: 'Tab 1',
        editor: <Editor height="90vh" defaultLanguage="javascript" defaultValue="console.log('Hello World!');"/>,
    }]);
    const [activeTab, setActiveTab] = useState(null);

    const handleTabClick = (tab) => {
        setActiveTab(tab);
    }

    const handleCloseTab = (tab) => {
        setTabs(tabs.filter(t => t !== tab));
        if (activeTab === tab) {
            setActiveTab(null);
        }
    }

    const handleNewTab = () => {
        const newTab = {
            title: 'New Tab',
            editor: <Editor height="90vh" defaultLanguage="javascript" defaultValue="console.log('Hello World!');"/>,
            language: 'javascript'
        };
        setTabs([...tabs, newTab]);
        setActiveTab(newTab);
    }

    const handleTabChange = (content) => {
        const updatedTabs = tabs.map(t => t === activeTab ? {...t, content} : t);
        setTabs(updatedTabs);
    }

    return (
        <div className="work-area">
            <TabBar tabs={tabs} activeTab={activeTab} onTabClick={handleTabClick} onCloseTab={handleCloseTab}
                    onNewTab={handleNewTab} onTabChange={handleTabChange}/>
            <div className="editor">
                {activeTab && activeTab.editor}
            </div>
        </div>
    );

}

export default WorkArea;
