import React from 'react';

import Editor from '@monaco-editor/react'

const MonacoEditor = (tab, setTabs) => {
    return (
        <div>
            {tab && <Editor
                height="90vh"
                defaultLanguage={tab.language}
                defaultValue={tab.content}
                onChange={(value) => {
                    setTabs(tab, value);
                }}
            />}
        </div>

    );
}

export default MonacoEditor;
