import React, {useState} from 'react';
import './WorkArea.css';

import TabBar from './TabBar';


function WorkArea() {

    const [tabs, setTabs] = useState([{
        title: 'Tab 1', content: 'This is tab 1', type: 'text',
    }
    ]);
    const [activeTab, setActiveTab] = useState(tabs[0]);

    const handleTabClick = (tab) => {
        setActiveTab(tab);
    }

    const handleCloseTab = (tab) => {
        if (tab === activeTab) {
            setActiveTab(null);
        }
        setTabs(tabs.filter(t => t !== tab));
    }

    const handleNewTab = () => {
        const newTab = {
            title: `Tab ${tabs.length + 1}`,
            content: '',
            type: 'text',
        };
        setTabs([...tabs, newTab]);
        setActiveTab(newTab);
    }

    const handleTabChange = (tab, value) => {
        const newTabs = tabs.map(t => t === tab ? {...t, content: value} : t);
        setTabs(newTabs);
    }


    return (<div className="work-area">
        <TabBar tabs={tabs} activeTab={activeTab} onTabClick={handleTabClick}
                onCloseTab={handleCloseTab} onNewTab={handleNewTab} onTabChange={handleTabChange}/>
    </div>);

}

export default WorkArea;
