import React from 'react';

import '/Tab.css'

function Tab({tab, active, onClick, onClose}) {
    return (
        <div className={`tab ${active ? 'active' : ''}`} onClick={() => onClick(tab)}>
            <span>{tab.title}</span>
            <button className="close" onClick={(e) => {
                e.stopPropagation();
                onClose(tab);
            }}>x
            </button>
        </div>
    );
}

export default Tab;
