import React, {useState} from 'react';
import './WorkArea.css';

import TabBar from './TabBar';
import Editor from '@monaco-editor/react';

function WorkArea() {

    const [tabs, setTabs] = useState([{
        title: 'Tab 1', content: 'This is tab 1', type: 'text',
    }]);
    const [activeTab, setActiveTab] = useState(null);

    const handleTabClick = (tab) => {
        setActiveTab(tab);
    }

    const handleCloseTab = (tab) => {
        setTabs(tabs.filter(t => t !== tab));
        if (activeTab === tab) {
            setActiveTab(null);
        }
    }

    const handleNewTab = () => {
        const newTab = {
            title: 'New Tab', content: '', type: 'text',
        };
        setTabs([...tabs, newTab]);
        setActiveTab(newTab);
    }

    const handleTabChange = (content) => {
        const updatedTabs = tabs.map(t => t === activeTab ? {...t, content} : t);
        setTabs(updatedTabs);
    }

    return (<div className="work-area">
        <TabBar tabs={tabs} activeTab={activeTab} onTabClick={handleTabClick}
                onCloseTab={handleCloseTab} onNewTab={handleNewTab} onTabChange={handleTabChange}/>
        <div className="editor-container">
            {activeTab && <Editor
                height="100%"
                defaultLanguage="javascript"
                defaultValue={activeTab.content}
                onChange={handleTabChange}
            />}
        </div>
    </div>);

}

export default WorkArea;
